mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM AS rn FROM (SELECT * FROM BOARD b JOIN (SELECT * FROM user_info i JOIN users u ON i.emp_no = u.emp_no) a ON b.id = a.id
ORDER BY b.board_no DESC ) t )
WHERE rn >= (#{cri.page}-1) * #{cri.perPageNum} +1 AND rn <= #{cri.page} * #{cri.perPageNum}

mapper.insert:

INSERT INTO BOARD (
			ID
		  , BOARD_NO
		  , BOARD_TYPE
		  , TITLE
		  , CONTENT
		  , INSERT_DATE

) VALUES (
			#{id}
		  , #{boardNo}
		  , #{boardType}
		  , #{title}
		  , #{content}
		  , sysdate

)

mapper.update:
UPDATE BOARD


		TITLE = #{title},


		content = #{content},

	UPDATE_DATE = #{updateDate}

WHERE BOARD_NO = #{boardNo}

UPDATE com
SET com_content = #{comContent}
WHERE com_no = #{comNo}
